export default {
  name: "selection",
  props: {
    selection: String
  },
  computed: mapGetters(["getSelectedOption"]),
  data() {
    return {
      selectedID: Number,
      faculties: faculties,
      specializations: activeSpecializations
    };
  },
  methods: {
    onChange(event){
      let facultyId;
      for(let i=0; i<faculties.length;i++){
        if(faculties[i].name==event.target.value){
          facultyId = faculties[i].facultateID;
          break;
        }
      }
      this.specializations =  facultyId==0 ? specializations : specializations.filter(s => s.facultateID == facultyId);
    }
  }
};
